import React, { Component } from 'react'

import axios from 'axios'
import { Link } from 'react-router-dom';

export default class Perfil extends Component {
    state = {
        users: []
    }

    async componentDidMount() {
        const res = axios.get('http:/localhost:4000/user/1');
        this.setState({ users: res.data });
        console.log(this.state.users.data);
    }

    render() {
        return (
            <div className="container col-7">
                <div className="card text-white">
                    <img src="https://k37.kn3.net/9E0D271BA.jpg" className="card-body-img" alt="..." />
                    <div className="card-img-overlay">
                        <div className="media h-100">
                            <img src={'https://laboratoriosniam.com/wp-content/uploads/2018/07/michael-dam-258165-unsplash_WEB2.jpg'} width="180" height="180" className="align-self-end rounded-circle mr-2 mt-2 border" alt="" />
                            <div className="media-body align-self-end ">
                                <h2 className="mt-0 p-3 mb-5">Gabriela Astudillo</h2>
                            </div>
                        </div>
                    </div>
                    <div class="card-body my-0 ml-auto">
                        <Link className="font-weight-bold p-3 m-0 border" to="/">Biografía</Link>
                        <Link className="font-weight-bold p-3 m-0 border">Información</Link>
                        <Link className="font-weight-bold p-3 m-0 border">Amigos</Link>
                        <Link className="font-weight-bold p-3 m-0 border">Fotos</Link>
                        <Link className="font-weight-bold p-3 m-0 border">Videos</Link>
                        <Link className="font-weight-bold p-3 m-0 border">Archivo</Link>                        
                        <Link className="font-weight-bold p-3 m-0 border">Más</Link>
                    </div>

                </div>
            </div>
        )
    }
}
