import React, { Component } from 'react'

export default class Comment extends Component {
    render() {
        return (
            <li className="list-group-item  p-2 pl-4 border-0">
                <div className="row">
                    <img src={'https://laboratoriosniam.com/wp-content/uploads/2018/07/michael-dam-258165-unsplash_WEB2.jpg'} width="38" height="38" className="rounded-circle float-left border-0 ml-2" alt="" />
                    <div className="col">
                        <div className="form-group">
                            <textarea className="form-control rounded-pill border-0 bg-light p-0 mr-2" rows="auto" disabled>
                            </textarea>
                        </div>
                    </div>
                </div>
            </li>
        )
    }
}
