import React, { Component } from 'react'

export default class Comment extends Component {
    render() {
        return (
            <li className="list-group-item  p-2 pl-4 border-0">

                <img src={'https://laboratoriosniam.com/wp-content/uploads/2018/07/michael-dam-258165-unsplash_WEB2.jpg'} width="38" height="38" className="rounded-circle float-left mr-2 border-0" alt="" />
                <div class="form-group">

                    <textarea class="form-control rounded-pill border-0 bg-light p-0"  rows="auto"></textarea>
                </div>
            </li>
        )
    }
}
